        set => this.RaiseAndSetIfChanged(ref _currentView, value);
    }
    public ReactiveCommand<Unit, UserControl> CadenBaseCommand { get; private set; }
    public ReactiveCommand<Unit, UserControl> CadenBaseGeneCommand { get; private set; }
    public ReactiveCommand<Unit, UserControl> SettingCommand { get; private set; }
    public MainViewModel()
    {
        CurrentView = new CadenBaseControl();
        CadenBaseCommand = ReactiveCommand.Create(() => CurrentView = new CadenBaseControl());
        CadenBaseGeneCommand = ReactiveCommand.Create(() => CurrentView = new CadenBaseGeneControl());
        SettingCommand = ReactiveCommand.Create(() => CurrentView = new SettingControl());
    }
}
